import matplotlib.pyplot as plt
import matplotlib
import numpy as np

font = {'family': 'times',
        'size': 16}
matplotlib.rc('font', **font)

#l = 20e-3 m
# Ns = 40000, 80000,12000

D = 8.4*1
L = 1

def getTxt(xs,t):
    Tg = 320
    Td = 280

    cste = (Tg+Td)/2
    def harmo(n,xs,t):
        kn = np.pi/L + 2*n*np.pi/L

        return (Td-Tg)/(0.5+n)/np.pi * np.exp(-D * kn**2 * t/1000) * np.sin(kn * xs)

    sol = xs * 0 + cste
    for i in range(50):
        sol += harmo(i,xs,t)
    return sol


